parameters.jsCode:
// Transform customer support tickets and prepare for AI categorization
const results = [];

for (const item of items) {
  try {
    const ticket = item.json;

    // Extract and clean ticket data
    const ticketData = {
      id: ticket.id || `ticket_${Date.now()}`,
      subject: ticket.subject || '',
      description: ticket.description || ticket.body || '',
      customer: {
        name: ticket.customer_name || ticket.name || 'Unknown',
        email: ticket.customer_email || ticket.email || '',
        priority: ticket.customer_priority || 'standard'
      },
      source: ticket.source || 'webhook',
      receivedAt: new Date().toISOString(),
      processedAt: new Date().toISOString()
    };

    // Prepare text for AI analysis
    const analysisText = `Subject: ${ticketData.subject}\n\nDescription: ${ticketData.description}`;

    // Extract keywords for team assignment
    const keywords = {
      billing: /billing|payment|invoice|charge|refund|subscription/i.test(analysisText),
      technical: /bug|error|crash|api|integration|technical|not working/i.test(analysisText),
      account: /login|password|access|account|profile|settings/i.test(analysisText),
      general: true // fallback
    };

    // Determine initial team assignment based on keywords
    let assignedTeam = 'general';
    if (keywords.billing) assignedTeam = 'billing';
    else if (keywords.technical) assignedTeam = 'technical';
    else if (keywords.account) assignedTeam = 'account';

    // Prepare structured output for next nodes
    results.push({
      json: {
        ...ticketData,
        analysisText,
        keywords,
        assignedTeam,
        status: 'pending_ai_analysis',
        metadata: {
          wordCount: analysisText.split(' ').length,
          hasAttachments: ticket.attachments && ticket.attachments.length > 0,
          originalPayload: ticket
        }
      }
    });

  } catch (error) {
    // Error handling - create error ticket entry
    results.push({
      json: {
        id: `error_${Date.now()}`,
        subject: 'Ticket Processing Error',
        description: `Failed to process incoming ticket: ${error.message}`,
        customer: { name: 'System', email: 'system@company.com' },
        assignedTeam: 'technical',
        status: 'processing_error',
        error: error.message,
        originalData: item.json,
        processedAt: new Date().toISOString()
      }
    });
  }
}

return results;

