// Transform customer support tickets and prepare for AI categorization
const ticketData = $input.all();

const transformedTickets = ticketData.map(ticket => {
  const data = ticket.json;

  return {
    json: {
      ticket_id: data.ticket_id || `ticket_${Date.now()}`,
      customer_name: data.customer_name || 'Unknown Customer',
      customer_email: data.customer_email || '',
      ticket_content: data.message || data.description || data.content || '',
      subject: data.subject || 'No Subject',
      priority: data.priority || 'medium',
      category: data.category || 'general',
      created_at: data.created_at || new Date().toISOString(),
      source: data.source || 'webhook',
      // Prepare text for AI analysis
      full_text: `Subject: ${data.subject || 'No Subject'}\n\nMessage: ${data.message || data.description || data.content || ''}`,
      original_data: data
    }
  };
});

return transformedTickets;

