parameters.jsCode:
// Transform customer support tickets and assign teams
const results = [];

for (const item of items) {
  try {
    const ticket = item.json;

    // Extract ticket data
    const ticketData = {
      id: ticket.id || `ticket_${Date.now()}`,
      subject: ticket.subject || '',
      description: ticket.description || ticket.body || '',
      customer: {
        name: ticket.customer_name || ticket.name || 'Unknown',
        email: ticket.customer_email || ticket.email || '',
        priority: ticket.priority || 'medium'
      },
      urgency: ticket.urgency || 'medium',
      sentiment: ticket.sentiment || 'neutral',
      category: ticket.category || 'general',
      processedAt: new Date().toISOString(),
      status: 'new'
    };

    // Team assignment logic based on keywords
    let assignedTeam = 'general';
    const content = (ticketData.subject + ' ' + ticketData.description).toLowerCase();

    // Technical keywords
    if (content.match(/\b(bug|error|crash|api|integration|technical|server|database|login|password)\b/)) {
      assignedTeam = 'technical';
    }
    // Billing keywords
    else if (content.match(/\b(billing|payment|invoice|refund|charge|subscription|price|cost)\b/)) {
      assignedTeam = 'billing';
    }
    // Sales keywords
    else if (content.match(/\b(upgrade|demo|trial|purchase|quote|sales|pricing|plan)\b/)) {
      assignedTeam = 'sales';
    }
    // Account management keywords
    else if (content.match(/\b(account|profile|settings|permissions|access|user)\b/)) {
      assignedTeam = 'account';
    }

    // Priority mapping for Slack notifications
    let slackPriority = 'normal';
    let slackColor = '#36a64f'; // green

    if (ticketData.urgency === 'high' || ticketData.customer.priority === 'high') {
      slackPriority = 'high';
      slackColor = '#ff0000'; // red
    } else if (ticketData.urgency === 'medium' || ticketData.customer.priority === 'medium') {
      slackPriority = 'medium';
      slackColor = '#ffaa00'; // orange
    }

    // Sentiment-based adjustments
    if (ticketData.sentiment === 'negative') {
      slackPriority = slackPriority === 'low' ? 'medium' : 'high';
      slackColor = ticketData.sentiment === 'negative' && slackPriority === 'high' ? '#cc0000' : slackColor;
    }

    results.push({
      json: {
        ...ticketData,
        assignedTeam,
        slackNotification: {
          priority: slackPriority,
          color: slackColor,
          channel: assignedTeam === 'technical' ? '#tech-support' : 
                  assignedTeam === 'billing' ? '#billing-support' :
                  assignedTeam === 'sales' ? '#sales-team' : '#general-support',
          urgentChannel: slackPriority === 'high' ? '#urgent-tickets' : null
        },
        retryConfig: {
          maxRetries: slackPriority === 'high' ? 5 : 3,
          retryDelay: slackPriority === 'high' ? 30000 : 60000 // ms
        }
      }
    });

  } catch (error) {
    // Error handling - create error ticket entry
    results.push({
      json: {
        id: `error_${Date.now()}`,
        error: true,
        errorMessage: error.message,
        originalData: item.json,
        processedAt: new Date().toISOString(),
        assignedTeam: 'technical',
        slackNotification: {
          priority: 'high',
          color: '#ff0000',
          channel: '#tech-support',
          urgentChannel: '#urgent-tickets'
        }
      }
    });
  }
}

return results;

