// Transform customer support tickets and assign teams based on keywords and AI categorization
const ticketData = $input.first();
const aiAnalysis = $input.last();

// Extract ticket information
const ticket = {
  id: ticketData.json.id || Date.now(),
  subject: ticketData.json.subject || '',
  description: ticketData.json.description || '',
  customer: ticketData.json.customer || 'Unknown',
  email: ticketData.json.email || '',
  timestamp: new Date().toISOString()
};

// Extract AI analysis
const analysis = {
  urgency: aiAnalysis.json.urgency || 'medium',
  sentiment: aiAnalysis.json.sentiment || 'neutral',
  reasoning: aiAnalysis.json.reasoning || 'No analysis available'
};

// Team assignment logic based on keywords
function assignTeam(subject, description, urgency) {
  const content = (subject + ' ' + description).toLowerCase();

  // Critical issues go to escalation team
  if (urgency === 'critical') {
    return 'escalation';
  }

  // Technical keywords
  if (content.includes('api') || content.includes('integration') || content.includes('bug') || content.includes('error')) {
    return 'technical';
  }

  // Billing keywords
  if (content.includes('billing') || content.includes('payment') || content.includes('invoice') || content.includes('refund')) {
    return 'billing';
  }

  // Account keywords
  if (content.includes('account') || content.includes('login') || content.includes('password') || content.includes('access')) {
    return 'account';
  }

  // Default to general support
  return 'general';
}

const assignedTeam = assignTeam(ticket.subject, ticket.description, analysis.urgency);

// Priority mapping for notifications
const priorityMap = {
  'critical': 'HIGH',
  'high': 'HIGH', 
  'medium': 'MEDIUM',
  'low': 'LOW'
};

return {
  json: {
    ticket: ticket,
    analysis: analysis,
    assignedTeam: assignedTeam,
    priority: priorityMap[analysis.urgency],
    slackChannel: `#support-${assignedTeam}`,
    processed: true
  }
};

