parameters.jsCode:
// Transform Google Sheets data into JSON and XML formats
const results = [];

for (const item of $input.all()) {
  // Validate required fields
  if (!item.json.name || !item.json.email) {
    console.log('Skipping invalid row:', item.json);
    continue;
  }

  // Transform for CRM format
  const crmData = {
    contact: {
      firstName: item.json.name?.split(' ')[0] || '',
      lastName: item.json.name?.split(' ').slice(1).join(' ') || '',
      email: item.json.email,
      phone: item.json.phone || '',
      company: item.json.company || ''
    },
    source: 'google_sheets',
    timestamp: new Date().toISOString()
  };

  // Transform for inventory format
  const inventoryData = {
    product: {
      sku: item.json.sku || '',
      name: item.json.product_name || '',
      quantity: parseInt(item.json.quantity) || 0,
      price: parseFloat(item.json.price) || 0
    },
    lastUpdated: new Date().toISOString()
  };

  // Transform for email marketing format
  const emailData = {
    subscriber: {
      email: item.json.email,
      firstName: item.json.name?.split(' ')[0] || '',
      tags: item.json.tags?.split(',') || [],
      customFields: {
        source: 'google_sheets',
        lastActivity: new Date().toISOString()
      }
    }
  };

  results.push({
    json: {
      originalData: item.json,
      crmData,
      inventoryData,
      emailData,
      rowIndex: item.json.row_number || 0
    }
  });
}

return results;

