parameters.jsCode:
// Transform Google Sheets data into JSON and XML formats
const results = [];

for (const item of items) {
  try {
    // Extract and validate data from Google Sheets
    const rowData = item.json;

    // Basic validation
    if (!rowData.id || !rowData.name) {
      throw new Error(`Missing required fields in row: ${JSON.stringify(rowData)}`);
    }

    // Create JSON format for CRM and inventory APIs
    const jsonFormat = {
      id: rowData.id,
      name: rowData.name,
      email: rowData.email || '',
      phone: rowData.phone || '',
      status: rowData.status || 'active',
      lastUpdated: new Date().toISOString(),
      source: 'google_sheets'
    };

    // Create XML format for email marketing platform
    const xmlFormat = `<?xml version="1.0" encoding="UTF-8"?>
<contact>
  <id>${rowData.id}</id>
  <name><![CDATA[${rowData.name}]]></name>
  <email>${rowData.email || ''}</email>
  <phone>${rowData.phone || ''}</phone>
  <status>${rowData.status || 'active'}</status>
  <lastUpdated>${new Date().toISOString()}</lastUpdated>
  <source>google_sheets</source>
</contact>`;

    // Return transformed data with both formats
    results.push({
      json: {
        originalData: rowData,
        jsonFormat: jsonFormat,
        xmlFormat: xmlFormat,
        processedAt: new Date().toISOString(),
        validationPassed: true
      }
    });

  } catch (error) {
    // Handle validation errors
    results.push({
      json: {
        originalData: item.json,
        error: error.message,
        processedAt: new Date().toISOString(),
        validationPassed: false
      }
    });
  }
}

return results;

