parameters.jsCode:
// Transform social media posts for AI content analysis
const results = [];

for (const item of items) {
  // Extract and normalize social media post data
  const postData = item.json;

  // Create standardized structure for AI analysis
  const transformedPost = {
    // Core identification
    postId: postData.id || postData.post_id,
    platform: postData.platform || 'unknown',

    // Content for AI analysis
    content: {
      text: postData.text || postData.content || postData.message || '',
      images: postData.images || postData.media?.filter(m => m.type === 'image') || [],
      videos: postData.videos || postData.media?.filter(m => m.type === 'video') || [],
      hashtags: postData.hashtags || [],
      mentions: postData.mentions || []
    },

    // Author information
    author: {
      id: postData.author?.id || postData.user_id,
      username: postData.author?.username || postData.username,
      followerCount: postData.author?.followers_count || 0,
      verified: postData.author?.verified || false
    },

    // Engagement metrics
    engagement: {
      likes: postData.likes_count || postData.likes || 0,
      shares: postData.shares_count || postData.retweets || postData.shares || 0,
      comments: postData.comments_count || postData.replies || 0
    },

    // Timestamps
    createdAt: postData.created_at || postData.timestamp || new Date().toISOString(),
    processedAt: new Date().toISOString(),

    // Analysis preparation flags
    requiresAnalysis: true,
    contentLength: (postData.text || postData.content || '').length,
    hasMedia: (postData.images?.length > 0 || postData.videos?.length > 0 || false),

    // Risk indicators for prioritization
    riskFactors: {
      hasExternalLinks: /https?:\/\//.test(postData.text || postData.content || ''),
      highEngagement: (postData.likes_count || 0) > 1000,
      newAccount: postData.author?.account_age_days < 30 || false,
      multipleHashtags: (postData.hashtags?.length || 0) > 5
    }
  };

  results.push({
    json: transformedPost
  });
}

return results;

