// Image resizing logic for web and mobile
const sharp = require('sharp');
const items = $input.all();
const results = [];

for (const item of items) {
  const imageBuffer = item.binary.imageData.data;

  // Create different sizes
  const sizes = {
    thumbnail: { width: 150, height: 150 },
    mobile: { width: 480, height: 320 },
    web: { width: 1200, height: 800 },
    social: { width: 1080, height: 1080 }
  };

  const resizedImages = {};

  for (const [sizeName, dimensions] of Object.entries(sizes)) {
    const resized = await sharp(imageBuffer)
      .resize(dimensions.width, dimensions.height, { fit: 'cover' })
      .jpeg({ quality: 85 })
      .toBuffer();

    resizedImages[sizeName] = {
      data: resized,
      mimeType: 'image/jpeg',
      fileName: `${item.json.name.split('.')[0]}_${sizeName}.jpg`
    };
  }

  results.push({
    json: {
      ...item.json,
      resizedImages: Object.keys(resizedImages),
      originalSize: imageBuffer.length
    },
    binary: {
      ...item.binary,
      ...resizedImages
    }
  });
}

return results;

