parameters.jsCode:
// Transform scraped competitor data into standardized format for pricing analysis
const items = $input.all();
const transformedData = [];

for (const item of items) {
  try {
    const aiResponse = JSON.parse(item.json.choices[0].message.content);

    // Calculate competitive pricing recommendations
    const competitorPrice = parseFloat(aiResponse.cleanPrice);
    const ourCostBasis = competitorPrice * 0.7; // Assume 30% margin
    const recommendedPrice = competitorPrice * 0.95; // Price 5% below competitor
    const profitMargin = ((recommendedPrice - ourCostBasis) / recommendedPrice) * 100;

    transformedData.push({
      productName: aiResponse.productName,
      competitorPrice: competitorPrice,
      recommendedPrice: recommendedPrice,
      profitMargin: profitMargin,
      availability: aiResponse.availability,
      priceChange: competitorPrice < (item.json.previousPrice || competitorPrice) ? 'decrease' : 'stable',
      timestamp: new Date().toISOString(),
      needsAlert: profitMargin < 15 || (item.json.previousPrice && competitorPrice < item.json.previousPrice * 0.9)
    });
  } catch (error) {
    console.error('Error processing item:', error);
  }
}

return transformedData.map(data => ({ json: data }));

