Competitor: {{ $json.competitor_name }}
Product Category: {{ $json.product_category || 'general' }}

parameters.jsCode:
// Transform scraped competitor data for pricing analysis
const results = [];

for (const item of items) {
  try {
    const competitorData = item.json;

    // Extract and standardize product information
    const productInfo = {
      // Product identification
      productId: competitorData.productId || competitorData.sku || competitorData.id,
      productName: competitorData.name || competitorData.title,
      competitor: competitorData.source || competitorData.competitor,

      // Pricing data
      currentPrice: parseFloat(competitorData.price) || 0,
      originalPrice: parseFloat(competitorData.originalPrice) || parseFloat(competitorData.price) || 0,
      currency: competitorData.currency || 'USD',

      // Availability
      inStock: competitorData.inStock !== false && competitorData.availability !== 'out-of-stock',
      stockLevel: competitorData.stockLevel || (competitorData.inStock ? 'available' : 'unavailable'),

      // Metadata
      scrapedAt: competitorData.scrapedAt || new Date().toISOString(),
      url: competitorData.url || competitorData.productUrl,

      // Calculate discount percentage if applicable
      discountPercent: competitorData.originalPrice && competitorData.price ? 
        Math.round(((parseFloat(competitorData.originalPrice) - parseFloat(competitorData.price)) / parseFloat(competitorData.originalPrice)) * 100) : 0,

      // Pricing analysis flags
      isPriceValid: !isNaN(parseFloat(competitorData.price)) && parseFloat(competitorData.price) > 0,
      needsReview: !competitorData.price || competitorData.price === 'N/A' || competitorData.price === '',

      // Processing timestamp
      processedAt: new Date().toISOString()
    };

    // Add competitive analysis markers
    productInfo.competitiveMetrics = {
      priceCategory: productInfo.currentPrice < 50 ? 'budget' : 
                    productInfo.currentPrice < 200 ? 'mid-range' : 'premium',
      hasDiscount: productInfo.discountPercent > 0,
      significantDiscount: productInfo.discountPercent > 20
    };

    results.push({
      json: productInfo
    });

  } catch (error) {
    // Handle malformed data gracefully
    results.push({
      json: {
        error: `Failed to process item: ${error.message}`,
        originalData: item.json,
        processedAt: new Date().toISOString(),
        needsReview: true
      }
    });
  }
}

return results;

