Parameters: {"nodeType":"nodes-base.code","config":{"jsCode":"// Analyze customer signup data and enrich with company profile
const results = [];

for (const item of items) {
  const customerData = item.json;

  // Extract company information
  const companyName = customerData.company || customerData.companyName || '';
  const email = customerData.email || '';
  const domain = email.split('@')[1] || '';

  // Determine company size based on available data
  let companySize = 'unknown';
  if (customerData.employees) {
    const empCount = parseInt(customerData.employees);
    if (empCount < 10) companySize = 'startup';
    else if (empCount < 50) companySize = 'small';
    else if (empCount < 500) companySize = 'medium';
    else companySize = 'enterprise';
  }

  // Industry classification based on domain or provided data
  let industry = customerData.industry || 'general';
  if (!customerData.industry && domain) {
    // Basic industry detection from common domain patterns
    if (domain.includes('tech') || domain.includes('software')) industry = 'technology';
    else if (domain.includes('health') || domain.includes('medical')) industry = 'healthcare';
    else if (domain.includes('finance') || domain.includes('bank')) industry = 'finance';
    else if (domain.includes('edu')) industry = 'education';
  }

  // Determine lead score and priority
  let leadScore = 50; // Base score
  let priority = 'medium';

  // Scoring logic
  if (companySize === 'enterprise') leadScore += 30;
  else if (companySize === 'medium') leadScore += 20;
  else if (companySize === 'small') leadScore += 10;

  if (['technology', 'finance', 'healthcare'].includes(industry)) leadScore += 15;

  if (customerData.revenue && parseInt(customerData.revenue.replace(/[^0-9]/g, '')) > 1000000) {
    leadScore += 25;
  }

  // Set priority based on score
  if (leadScore >= 80) priority = 'high';
  else if (leadScore >= 60) priority = 'medium';
  else priority = 'low';

  // Determine appropriate demo type and timeline
  let demoType = 'standard';
  let demoTimeline = '7-days';

  if (companySize === 'enterprise' || priority === 'high') {
    demoType = 'executive';
    demoTimeline = '2-days';
  } else if (companySize === 'medium') {
    demoType = 'business';
    demoTimeline = '3-days';
  }

  // Email sequence selection
  const emailSequence = `${industry}_${companySize}`;

  results.push({
    json: {
      // Original customer data
      ...customerData,

      // Enriched profile data
      companyProfile: {
        name: companyName,
        domain: domain,
        size: companySize,
        industry: industry,
        estimatedEmployees: customerData.employees || 'unknown'
      },

      // Lead qualification
      leadScore: leadScore,
      priority: priority,

      // Personalization parameters
      personalization: {
        emailSequence: emailSequence,
        demoType: demoType,
        demoTimeline: demoTimeline,
        industryFocus: industry,
        sizeSegment: companySize
      },

      // Workflow routing flags
      routing: {
        requiresHighTouchSales: priority === 'high',
        autoScheduleDemo: companySize !== 'startup',
        enableAdvancedTracking: ['medium', 'enterprise'].includes(companySize),
        escalateToSales: leadScore >= 75
      },

      // Processing metadata
      processedAt: new Date().toISOString(),
      workflowStage: 'profile_analysis_complete'
    }
  });
}

return results;","language":"javaScript"}}

