parameters.jsCode:
// Analyze customer signup data and enrich with company profile
const results = [];

for (const item of items) {
  const customerData = item.json;

  // Extract company information
  const companyName = customerData.company || customerData.companyName || '';
  const email = customerData.email || '';
  const domain = email.split('@')[1] || '';

  // Determine company size based on available data
  let companySize = 'unknown';
  if (customerData.employees) {
    const empCount = parseInt(customerData.employees);
    if (empCount < 10) companySize = 'startup';
    else if (empCount < 50) companySize = 'small';
    else if (empCount < 500) companySize = 'medium';
    else companySize = 'enterprise';
  }

  // Industry classification based on domain or provided data
  let industry = customerData.industry || 'general';
  if (!customerData.industry && domain) {
    // Basic industry detection from common domain patterns
    if (domain.includes('tech') || domain.includes('software')) industry = 'technology';
    else if (domain.includes('health') || domain.includes('medical')) industry = 'healthcare';
    else if (domain.includes('finance') || domain.includes('bank')) industry = 'finance';
    else if (domain.includes('edu')) industry = 'education';
  }

  // Determine lead score and priority
  let leadScore = 50; // Base score
  let priority = 'medium';

  // Scoring logic
  if (companySize === 'enterprise') leadScore += 30;
  else if (companySize === 'medium') leadScore += 20;
  else if (companySize === 'small') leadScore += 10;

  if (['technology', 'finance', 'healthcare'].includes(industry)) leadScore += 15;

  if (customerData.revenue && parseInt(customerData.revenue.replace(/[^0-9]/g, '')) > 1000000) {
    leadScore += 25;
  }

  // Set priority based on score
  if (leadScore >= 80) priority = 'high';
  else if (leadScore >= 60) priority = 'medium';
  else priority = 'low';

  // Determine appropriate demo type and timeline
  let demoType = 'standard';
  let demoTimeline = '7-days';

  if (companySize === 'enterprise' || priority === 'high') {
    demoType = 'executive';
    demoTimeline = '2-days';
  } else if (companySize === 'medium') {
    demoType = 'business';
    demoTimeline = '3-days';
  }

  // Email sequence selection
  const emailSequence = `${industry}_${companySize}`;

  results.push({
    json: {
      // Original customer data
      ...customerData,

      // Enriched profile data
      companyProfile: {
        name: companyName,
        domain: domain,
        size: companySize,
        industry: industry,
        estimatedEmployees: customerData.employees || 'unknown'
      },

      // Lead qualification
      leadScore: leadScore,
      priority: priority,

      // Personalization parameters
      personalization: {
        emailSequence: emailSequence,
        demoType: demoType,
        demoTimeline: demoTimeline,
        industryFocus: industry,
        sizeSegment: companySize
      },

      // Workflow routing flags
      routing: {
        requiresHighTouchSales: priority === 'high',
        autoScheduleDemo: companySize !== 'startup',
        enableAdvancedTracking: ['medium', 'enterprise'].includes(companySize),
        escalateToSales: leadScore >= 75
      },

      // Processing metadata
      processedAt: new Date().toISOString(),
      workflowStage: 'profile_analysis_complete'
    }
  });
}

return results;

// ‚ö†Ô∏è CRITICAL: Webhook data is nested under 'body' property!
// Connect this Code node after a Webhook node for customer signup processing

// Access webhook payload data - it's under .body, not directly under .json
const webhookData = items[0].json.body;  // ‚úÖ CORRECT
const headers = items[0].json.headers;   // HTTP headers
const query = items[0].json.query;       // Query parameters

// Process customer signup data for intelligent onboarding
try {
  // Validate required signup fields
  if (!webhookData.email) {
    throw new Error('Missing required field: email');
  }
  if (!webhookData.company) {
    throw new Error('Missing required field: company');
  }

  // Extract and normalize customer data
  const customerData = {
    email: webhookData.email.toLowerCase().trim(),
    firstName: webhookData.firstName || webhookData.first_name || '',
    lastName: webhookData.lastName || webhookData.last_name || '',
    company: webhookData.company.trim(),
    jobTitle: webhookData.jobTitle || webhookData.job_title || '',
    phone: webhookData.phone || '',
    website: webhookData.website || webhookData.company_website || '',
    employees: webhookData.employees || webhookData.company_size || null,
    industry: webhookData.industry || '',
    source: webhookData.source || query.source || 'direct_signup'
  };

  // Determine company size category for personalization
  let companySizeCategory = 'unknown';
  if (customerData.employees) {
    const empCount = parseInt(customerData.employees);
    if (empCount <= 10) companySizeCategory = 'startup';
    else if (empCount <= 50) companySizeCategory = 'small';
    else if (empCount <= 500) companySizeCategory = 'medium';
    else companySizeCategory = 'enterprise';
  }

  // Extract domain for company analysis
  const emailDomain = customerData.email.split('@')[1];
  const websiteDomain = customerData.website ? 
    customerData.website.replace(/^https?:\/\/(www\.)?/, '').split('/')[0] : 
    emailDomain;

  // Determine lead priority based on company size and industry
  let leadPriority = 'medium';
  if (companySizeCategory === 'enterprise' || 
      ['technology', 'finance', 'healthcare', 'saas'].includes(customerData.industry.toLowerCase())) {
    leadPriority = 'high';
  } else if (companySizeCategory === 'startup') {
    leadPriority = 'low';
  }

  // Prepare data for next workflow steps
  const processedData = {
    customer: customerData,
    analysis: {
      companySizeCategory,
      emailDomain,
      websiteDomain,
      leadPriority,
      requiresApiEnrichment: !customerData.industry || !customerData.employees,
      personalizedEmailSequence: `${companySizeCategory}_${customerData.industry || 'general'}`,
      suggestedDemoType: companySizeCategory === 'enterprise' ? 'custom' : 'standard'
    },
    workflow: {
      signupId: crypto.randomUUID(),
      processedAt: DateTime.now().toISO(),
      nextSteps: [
        'enrich_company_data',
        'send_welcome_email',
        'schedule_demo_followup'
      ],
      escalateToSales: leadPriority === 'high'
    },
    metadata: {
      requestId: headers['x-request-id'] || crypto.randomUUID(),
      httpMethod: items[0].json.httpMethod,
      webhookPath: items[0].json.webhookPath,
      userAgent: headers['user-agent'] || '',
      ipAddress: headers['x-forwarded-for'] || headers['x-real-ip'] || '',
      timestamp: DateTime.now().toISO()
    }
  };

  // Return processed signup data for onboarding workflow
  return [{
    json: processedData
  }];

} catch (error) {
  // Return error response for failed signup processing
  return [{
    json: {
      status: 'error',
      error: error.message,
      originalData: webhookData,
      timestamp: DateTime.now().toISO(),
      requiresManualReview: true
    }
  }];
}

