  company: signupData.company || signupData.companyName,
  phone: signupData.phone,
  website: signupData.website,
  signupDate: new Date().toISOString(),
  source: signupData.source || 'website'
};

// Prepare data for company enrichment API
const enrichmentQuery = {
  domain: customer.website || (customer.email ? customer.email.split('@')[1] : null),
  company: customer.company,
  email: customer.email
};

return [{
  json: {
    ...customer,
    enrichmentQuery,
    workflowId: $workflow.id,
    executionId: $execution.id
  }
}];

// Analyzes customer signup data to determine company profile, industry classification, and personalization parameters for the onboarding workflow

const customerData = $('Process Signup Data').first().json;
const companyData = $input.first().json;

// Analyze company size and industry
let companyProfile = {
  size: 'unknown',
  industry: 'unknown',
  tier: 'standard',
  priority: 'medium',
  demoType: 'standard'
};

if (companyData && !companyData.error) {
  // Determine company size
  const employees = companyData.metrics?.employees || 0;
  if (employees > 1000) {
    companyProfile.size = 'enterprise';
    companyProfile.tier = 'enterprise';
    companyProfile.priority = 'high';
    companyProfile.demoType = 'executive';
  } else if (employees > 100) {
    companyProfile.size = 'medium';
    companyProfile.tier = 'business';
    companyProfile.priority = 'medium';
    companyProfile.demoType = 'business';
  } else if (employees > 10) {
    companyProfile.size = 'small';
    companyProfile.tier = 'professional';
    companyProfile.priority = 'medium';
    companyProfile.demoType = 'standard';
  } else {
    companyProfile.size = 'startup';
    companyProfile.tier = 'starter';
    companyProfile.priority = 'low';
    companyProfile.demoType = 'self-service';
  }

  // Extract industry
  companyProfile.industry = companyData.category?.industry || companyData.tags?.[0] || 'technology';
}

// Determine if high-value prospect
const isHighValue = companyProfile.size === 'enterprise' || companyProfile.size === 'medium';

// Calculate lead score
let leadScore = 50; // Base score
if (companyProfile.size === 'enterprise') leadScore += 40;
else if (companyProfile.size === 'medium') leadScore += 25;
else if (companyProfile.size === 'small') leadScore += 10;

if (['technology', 'software', 'saas'].includes(companyProfile.industry.toLowerCase())) leadScore += 15;

return [{
  json: {
    ...customerData,
    companyProfile,
    companyData: companyData || {},
    isHighValue,
    leadScore,
    personalizedContent: {
      emailTemplate: `${companyProfile.size}_${companyProfile.industry}`,
      demoLength: companyProfile.size === 'enterprise' ? 60 : 30,
      followUpSequence: companyProfile.tier
    }
  }
}];

