  firstName: signupData.firstName || signupData.first_name,
  lastName: signupData.lastName || signupData.last_name,
  company: signupData.company || signupData.companyName,
  phone: signupData.phone,
  website: signupData.website,
  signupDate: new Date().toISOString(),
  source: signupData.source || 'website'
};

// Prepare data for company enrichment API
const enrichmentQuery = {
  domain: customer.website || (customer.email ? customer.email.split('@')[1] : null),
  company: customer.company,
  email: customer.email
};

return [{
