parameters.jsCode:
// Transform Zoom transcript data into structured action items
const results = [];

for (const item of items) {
  const transcript = item.json.transcript || '';
  const meetingTitle = item.json.meeting_topic || 'Meeting';
  const meetingDate = item.json.start_time || new Date().toISOString();

  // Extract key decisions and action items from transcript
  const decisions = [];
  const actionItems = [];

  // Simple keyword-based extraction for decisions
  const decisionKeywords = ['decided', 'agreed', 'concluded', 'resolved', 'determined'];
  const actionKeywords = ['action item', 'todo', 'follow up', 'will do', 'responsible for', 'by next', 'deadline'];

  const sentences = transcript.split(/[.!?]+/);

  sentences.forEach(sentence => {
    const lowerSentence = sentence.toLowerCase();

    // Check for decisions
    if (decisionKeywords.some(keyword => lowerSentence.includes(keyword))) {
      decisions.push({
        text: sentence.trim(),
        extractedAt: new Date().toISOString()
      });
    }

    // Check for action items
    if (actionKeywords.some(keyword => lowerSentence.includes(keyword))) {
      // Try to extract deadline information
      let deadline = null;
      const deadlinePatterns = [
        /by\s+(\w+day)/i,
        /due\s+(\w+day)/i,
        /before\s+(\w+day)/i,
        /next\s+(week|month)/i
      ];

      deadlinePatterns.forEach(pattern => {
        const match = sentence.match(pattern);
        if (match) {
          deadline = match[1];
        }
      });

      actionItems.push({
        text: sentence.trim(),
        deadline: deadline,
        status: 'pending',
        extractedAt: new Date().toISOString()
      });
    }
  });

  // Create summary object
  const summary = {
    meetingTitle: meetingTitle,
    meetingDate: meetingDate,
    keyDecisions: decisions,
    actionItems: actionItems,
    totalDecisions: decisions.length,
    totalActionItems: actionItems.length,
    processedAt: new Date().toISOString()
  };

  results.push({
    json: summary
  });
}

return results;

