parameters.jsCode:
// Transform Zoom transcript analysis into structured action items for project management
const aiResponse = $('Analyze Transcript').item.json.choices[0].message.content;
let parsedData;

try {
  parsedData = JSON.parse(aiResponse);
} catch (error) {
  // Fallback parsing if JSON is malformed
  parsedData = {
    decisions: [],
    action_items: [],
    summary: aiResponse
  };
}

// Transform action items into format suitable for project management tools
const transformedItems = [];

if (parsedData.action_items && Array.isArray(parsedData.action_items)) {
  parsedData.action_items.forEach((item, index) => {
    transformedItems.push({
      id: `action_${index + 1}`,
      title: item.item || `Action Item ${index + 1}`,
      description: `Meeting Decision: ${parsedData.summary}\n\nAction: ${item.item}`,
      assignee: item.assignee || 'Unassigned',
      due_date: item.deadline || null,
      priority: item.priority || 'medium',
      source: 'zoom_meeting',
      meeting_date: new Date().toISOString().split('T')[0]
    });
  });
}

return {
  action_items: transformedItems,
  decisions: parsedData.decisions || [],
  meeting_summary: parsedData.summary || '',
  total_items: transformedItems.length
};

