parameters.jsCode:
// Calculate percentage price changes for SUI, ETH, and BTC over the last hour
const currentPrices = $input.first().json;
const memory = $('Price History Memory').first().json;

// Extract current prices
const prices = {
  sui: currentPrices.sui?.usd || 0,
  ethereum: currentPrices.ethereum?.usd || 0,
  bitcoin: currentPrices.bitcoin?.usd || 0
};

// Get previous prices from memory (if available)
const previousPrices = memory?.previousPrices || prices;

// Calculate percentage changes
const changes = {
  sui: previousPrices.sui ? ((prices.sui - previousPrices.sui) / previousPrices.sui) * 100 : 0,
  ethereum: previousPrices.ethereum ? ((prices.ethereum - previousPrices.ethereum) / previousPrices.ethereum) * 100 : 0,
  bitcoin: previousPrices.bitcoin ? ((prices.bitcoin - previousPrices.bitcoin) / previousPrices.bitcoin) * 100 : 0
};

// Check if any change exceeds 3%
const significantChanges = [];
if (Math.abs(changes.sui) >= 3) significantChanges.push({ coin: 'SUI', change: changes.sui, price: prices.sui });
if (Math.abs(changes.ethereum) >= 3) significantChanges.push({ coin: 'ETH', change: changes.ethereum, price: prices.ethereum });
if (Math.abs(changes.bitcoin) >= 3) significantChanges.push({ coin: 'BTC', change: changes.bitcoin, price: prices.bitcoin });

return {
  currentPrices: prices,
  previousPrices: previousPrices,
  changes: changes,
  significantChanges: significantChanges,
  hasSignificantChange: significantChanges.length > 0,
  timestamp: new Date().toISOString()
};

