for (const item of $input.all()) {
  const data = item.json;

  // Calculate percentage change over 1 hour
  const currentPrice = parseFloat(data.current_price);
  const oneHourAgoPrice = parseFloat(data.price_1h_ago);

  if (!currentPrice || !oneHourAgoPrice) {
    continue; // Skip if price data is missing
  }

  const percentChange = ((currentPrice - oneHourAgoPrice) / oneHourAgoPrice) * 100;
  const absPercentChange = Math.abs(percentChange);

  // Determine if alert is needed
  const needsAlert = absPercentChange >= THRESHOLD;

  results.push({
    json: {
      symbol: data.symbol.toUpperCase(),
      current_price: currentPrice,
      price_1h_ago: oneHourAgoPrice,
      percent_change_1h: parseFloat(percentChange.toFixed(2)),
      abs_percent_change: parseFloat(absPercentChange.toFixed(2)),
      needs_alert: needsAlert,
      direction: percentChange > 0 ? 'up' : 'down',
      threshold_exceeded: needsAlert,
      processedAt: new Date().toISOString()
    }
  });
}

return results;

