parameters.jsCode:
// Transform agent output into structured data for Google Sheets
const items = [];

// Process agent response
if ($input.all().length > 0) {
  const agentData = $input.all()[0].json;

  // Extract LinkedIn posts
  if (agentData.linkedinPosts) {
    agentData.linkedinPosts.forEach(post => {
      items.push({
        platform: 'LinkedIn',
        url: post.url,
        title: post.title || 'LinkedIn Post',
        engagement: post.likes || 0,
        type: 'Post'
      });
    });
  }

  // Extract YouTube videos
  if (agentData.youtubeVideos) {
    agentData.youtubeVideos.forEach(video => {
      items.push({
        platform: 'YouTube',
        url: video.url,
        title: video.title || 'YouTube Video',
        engagement: video.views || 0,
        type: 'Video'
      });
    });
  }
}

return items;

