const hiringManagers = [];

for (const job of jobs) {
  const jobData = job.json;

  // Extract hiring manager information from job posting
  if (jobData.hiringManager || jobData.recruiter || jobData.contactPerson) {
    const manager = {
      jobTitle: jobData.title || 'N/A',
      company: jobData.company || 'N/A',
      hiringManager: jobData.hiringManager || jobData.recruiter || jobData.contactPerson || 'N/A',
      jobUrl: jobData.url || 'N/A',
      scrapedDate: new Date().toISOString().split('T')[0]
    };

    hiringManagers.push(manager);
  }
}

// Return formatted data for Google Sheets
return hiringManagers.map(manager => ({
  json: {
    'Job Title': manager.jobTitle,
    'Company': manager.company,
    'Hiring Manager': manager.hiringManager,
    'Job URL': manager.jobUrl,
    'Scraped Date': manager.scrapedDate
  }
}));

