parameters.jsCode:
// Extract hiring managers from LinkedIn job scraping data
const results = [];

for (const item of items) {
  const jobData = item.json;

  // Extract hiring manager information from various possible fields
  let hiringManager = null;
  let hiringManagerEmail = null;
  let hiringManagerLinkedIn = null;

  // Check common fields where hiring manager info might be stored
  if (jobData.hiringManager) {
    hiringManager = jobData.hiringManager;
  } else if (jobData.recruiter) {
    hiringManager = jobData.recruiter;
  } else if (jobData.contactPerson) {
    hiringManager = jobData.contactPerson;
  } else if (jobData.poster) {
    hiringManager = jobData.poster;
  }

  // Extract email if available
  if (jobData.hiringManagerEmail || jobData.recruiterEmail || jobData.contactEmail) {
    hiringManagerEmail = jobData.hiringManagerEmail || jobData.recruiterEmail || jobData.contactEmail;
  }

  // Extract LinkedIn profile if available
  if (jobData.hiringManagerProfile || jobData.recruiterProfile || jobData.posterProfile) {
    hiringManagerLinkedIn = jobData.hiringManagerProfile || jobData.recruiterProfile || jobData.posterProfile;
  }

  // Only add to results if we found a hiring manager
  if (hiringManager) {
    results.push({
      json: {
        jobTitle: jobData.title || jobData.jobTitle || 'N/A',
        company: jobData.company || jobData.companyName || 'N/A',
        hiringManager: hiringManager,
        hiringManagerEmail: hiringManagerEmail || 'N/A',
        hiringManagerLinkedIn: hiringManagerLinkedIn || 'N/A',
        jobUrl: jobData.url || jobData.jobUrl || 'N/A',
        location: jobData.location || 'N/A',
        scrapedAt: new Date().toISOString(),
        jobId: jobData.id || jobData.jobId || `job_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
      }
    });
  }
}

// If no hiring managers found, return empty result with note
if (results.length === 0) {
  results.push({
    json: {
      note: 'No hiring managers found in the scraped data',
      totalJobsProcessed: items.length,
      processedAt: new Date().toISOString()
    }
  });
}

return results;

