parameters.jsCode:
// Transform GitHub PR data into structured summaries
const results = [];

// Group PRs by repository or category if needed
const prSummary = {
  totalPRs: items.length,
  repositories: {},
  summaryText: '',
  weekRange: ''
};

// Calculate week range
const now = new Date();
const weekStart = new Date(now.setDate(now.getDate() - 7));
const weekEnd = new Date();
prSummary.weekRange = `${weekStart.toLocaleDateString()} - ${weekEnd.toLocaleDateString()}`;

// Process each PR
for (const item of items) {
  const pr = item.json;
  const repoName = pr.repository?.name || pr.base?.repo?.name || 'Unknown';

  if (!prSummary.repositories[repoName]) {
    prSummary.repositories[repoName] = {
      count: 0,
      titles: []
    };
  }

  prSummary.repositories[repoName].count++;
  prSummary.repositories[repoName].titles.push({
    title: pr.title,
    author: pr.user?.login || pr.merged_by?.login || 'Unknown',
    mergedAt: pr.merged_at,
    url: pr.html_url
  });
}

// Create summary text for Slack
let summaryLines = [`ðŸ“Š Weekly PR Summary (${prSummary.weekRange})`, `Total PRs merged: ${prSummary.totalPRs}`, ''];

for (const [repo, data] of Object.entries(prSummary.repositories)) {
  summaryLines.push(`ðŸ”§ ${repo} (${data.count} PRs):`);
  data.titles.forEach(pr => {
    summaryLines.push(`  â€¢ ${pr.title} by @${pr.author}`);
  });
  summaryLines.push('');
}

prSummary.summaryText = summaryLines.join('\n');

// Create structured data for Notion
const notionData = {
  title: `PR Summary - Week of ${prSummary.weekRange}`,
  totalCount: prSummary.totalPRs,
  repositories: Object.entries(prSummary.repositories).map(([name, data]) => ({
    name,
    count: data.count,
    prs: data.titles
  })),
  generatedAt: new Date().toISOString()
};

results.push({
  json: {
    // For Slack posting
    slackMessage: prSummary.summaryText,
    // For Notion posting
    notionData: notionData,
    // Raw summary data
    summary: prSummary,
    processedAt: new Date().toISOString()
  }
});

return results;

