// Transform trending topics data
const results = [];

for (const item of items) {
  results.push({
    json: {
      id: item.json.id,
      topic: item.json.text,
      engagement: item.json.public_metrics,
      processedAt: new Date().toISOString(),
      hashtags: item.json.entities?.hashtags || [],
      mentions: item.json.entities?.mentions || [],
      relevanceScore: Math.random() * 100 // Placeholder for actual scoring
    }
  });
}

return results;
