// Read file from directory and extract numerical values
const fs = require('fs');
const path = require('path');

// Configure your file path here
const filePath = '/path/to/your/file.txt'; // Update this path

try {
  // Read file content
  const fileContent = fs.readFileSync(filePath, 'utf8');

  // Extract all numerical values using regex
  const numbers = fileContent.match(/\d+(?:\.\d+)?/g);

  if (!numbers || numbers.length === 0) {
    return [{ json: { error: 'No numerical values found in file', average: 0, numbers: [] } }];
  }

  // Convert to numbers and calculate average
  const numericValues = numbers.map(num => parseFloat(num));
  const sum = numericValues.reduce((acc, num) => acc + num, 0);
  const average = sum / numericValues.length;

  return [{
    json: {
      filePath: filePath,
      numbersFound: numericValues.length,
      numbers: numericValues,
      sum: sum,
      average: average,
      isAbove100: average > 100,
      differenceFrom100: average < 100 ? (100 - average) : 0
    }
  }];

} catch (error) {
  return [{ json: { error: error.message, average: 0 } }];
}

// Route data based on average value
const results = [];

for (const item of items) {
  const average = item.json.average || 0;

  if (average > 100) {
    // Route to Slack (output 0)
    results.push({
      json: {
        ...item.json,
        action: 'slack',
        message: `Weekly analysis complete! Average value is ${average.toFixed(2)}, which is above the 100 threshold.`
      }
    });
  } else {
    // Route to Email (output 1) 
    const difference = 100 - average;
    results.push({
      json: {
        ...item.json,
        action: 'email',
        difference: difference,
        message: `Weekly analysis shows average of ${average.toFixed(2)}, which is ${difference.toFixed(2)} points below the 100 threshold.`
      }
    });
  }
}

return results;

