// Transform social media post data for AI content analysis
const items = $input.all();

return items.map(item => {
  const postData = item.json;

  return {
    json: {
      postId: postData.id || postData.post_id,
      content: postData.content || postData.text || postData.message,
      platform: postData.platform || 'unknown',
      author: postData.author || postData.username || postData.user,
      timestamp: postData.timestamp || postData.created_at || new Date().toISOString(),
      url: postData.url || postData.permalink,
      metadata: {
        likes: postData.likes || 0,
        shares: postData.shares || 0,
        comments: postData.comments || 0,
        hashtags: postData.hashtags || [],
        mentions: postData.mentions || []
      }
    }
  };
});

