// Generate daily moderation report summary
const items = $input.all();
const data = items.map(item => item.json);

const totalPosts = data.length;
const flaggedPosts = data.filter(post => post.flagged).length;
const cleanPosts = totalPosts - flaggedPosts;

const severityBreakdown = {
  high: data.filter(post => post.severity === 'high').length,
  medium: data.filter(post => post.severity === 'medium').length,
  low: data.filter(post => post.severity === 'low').length
};

const platformBreakdown = {};
data.forEach(post => {
  platformBreakdown[post.platform] = (platformBreakdown[post.platform] || 0) + 1;
});

const flaggedItems = data.filter(post => post.flagged).slice(0, 10); // Top 10 flagged items

return [{
  json: {
    reportDate: new Date().toISOString().split('T')[0],
    summary: {
      totalPosts,
      flaggedPosts,
      cleanPosts,
      flaggedPercentage: ((flaggedPosts / totalPosts) * 100).toFixed(2)
    },
    severityBreakdown,
    platformBreakdown,
    flaggedItems: flaggedItems.map(item => ({
      postId: item.post_id,
      platform: item.platform,
      author: item.author,
      severity: item.severity,
      categories: JSON.parse(item.categories || '[]'),
      explanation: item.explanation,
      content: item.content.substring(0, 100) + '...'
    }))
  }
}];
