// Clean and format attendee names for LinkedIn search
const rawName = $input.item.json.attendees || '';

// Remove titles, company info, and clean the name
const cleanName = rawName
  .replace(/\b(Dr|Mr|Ms|Mrs|Prof|CEO|CTO|VP|Director)\b\.?/gi, '')
  .replace(/\s*\([^)]*\)\s*/g, '') // Remove parentheses content
  .replace(/\s*,.*$/g, '') // Remove everything after comma
  .replace(/\s+/g, ' ') // Normalize spaces
  .trim();

// Split into first and last name
const nameParts = cleanName.split(' ');
const firstName = nameParts[0] || '';
const lastName = nameParts.slice(1).join(' ') || '';

return {
  fullName: cleanName,
  firstName: firstName,
  lastName: lastName,
  searchQuery: `${firstName} ${lastName}`.trim()
};

// Generate personalized intro notes based on available data
const name = $input.item.json.fullName || 'Unknown';
const linkedinData = $input.item.json.body || '';

// Extract basic info from LinkedIn search results
let title = '';
let company = '';
let location = '';

// Simple regex patterns to extract info
const titleMatch = linkedinData.match(/<span[^>]*class="[^"]*title[^"]*"[^>]*>([^<]+)</i);
const companyMatch = linkedinData.match(/<span[^>]*class="[^"]*company[^"]*"[^>]*>([^<]+)</i);
const locationMatch = linkedinData.match(/<span[^>]*class="[^"]*location[^"]*"[^>]*>([^<]+)</i);

if (titleMatch) title = titleMatch[1].trim();
if (companyMatch) company = companyMatch[1].trim();
if (locationMatch) location = locationMatch[1].trim();

// Generate conversation starters
const introNotes = [];

if (title) {
  introNotes.push(`Ask about their role as ${title}`);
}

if (company) {
  introNotes.push(`Discuss trends in ${company}'s industry`);
}

if (location) {
  introNotes.push(`Mention connection to ${location}`);
}

// Add generic conversation starters
introNotes.push('Ask about their conference expectations');
introNotes.push('Discuss industry challenges and opportunities');
introNotes.push('Share relevant experience or insights');

return {
  name: name,
  title: title || 'Title not found',
  company: company || 'Company not found',
  location: location || 'Location not found',
  introNotes: introNotes.slice(0, 3), // Limit to top 3 notes
  profileFound: !!(title || company)
};
