// Transform Zoom transcript analysis into structured action items for project management
const aiResponse = $input.first().json;

// Parse AI response if it's a string
let analysisData;
try {
  analysisData = typeof aiResponse.choices[0].message.content === 'string' 
    ? JSON.parse(aiResponse.choices[0].message.content)
    : aiResponse.choices[0].message.content;
} catch (error) {
  // Fallback if parsing fails
  analysisData = { action_items: [], decisions: [] };
}

// Transform action items for project management tool
const transformedItems = [];

if (analysisData.action_items && Array.isArray(analysisData.action_items)) {
  analysisData.action_items.forEach((item, index) => {
    transformedItems.push({
      action_item: item.task || `Action Item ${index + 1}`,
      assignee: item.assignee || 'Unassigned',
      deadline_date: item.deadline || new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0], // Default to 1 week from now
      description: `Meeting Decision Context: ${analysisData.decisions ? analysisData.decisions.join('; ') : 'No decisions recorded'}`,
      priority: 'medium',
      source: 'zoom_meeting_transcript'
    });
  });
}

// If no action items found, create a summary task
if (transformedItems.length === 0) {
  transformedItems.push({
    action_item: 'Review Meeting Summary',
    assignee: 'Team Lead',
    deadline_date: new Date(Date.now() + 3 * 24 * 60 * 60 * 1000).toISOString().split('T')[0], // 3 days from now
    description: `Key Decisions: ${analysisData.decisions ? analysisData.decisions.join('; ') : 'No specific decisions recorded'}`,
    priority: 'low',
    source: 'zoom_meeting_transcript'
  });
}

return transformedItems.map(item => ({ json: item }));
